$sourcePath = "D:\DCIM\100EOS_R"
$targetBasePath = "$env:USERPROFILE\"
$smbPath = "\\SmbPath\"
$syncFiles = 0
$keepSourceFile = $true
$jpgFiles = ".jpg", ".jpeg"
$rawFiles = ".cr2", ".cr3"
$videoFiles = ".mp4"

#(https://ss64.com/ps/messagebox.html)
function show_message {
    param(
        $msgBody = 'message'
       ,$msgTitle = 'title'
       ,$msgButton = 'OK'
       ,$msgImage = 'None'
    )
    $Result = [System.Windows.MessageBox]::Show($msgBody, $msgTitle, $msgButton, $msgImage)

    return $Result
}

#
function move_file {
    param($SourceFile, $TargetFile, $KeepSource)

    if ((Test-Path -LiteralPath (Split-Path -LiteralPath $targetFile)) -eq $false) {
        mkdir -Path $targetPath
    }
    if ((Test-Path -LiteralPath $targetFile) -eq $false) {
        if ($keepSource -eq $true) {
            Copy-Item -Path $sourceFile -Destination $targetFile
            "Copy $_ To $targetPath\$_"
        } else {
            Move-Item -Path $sourceFile -Destination $targetFile
            "Move $_ To $targetPath\$_"
        }
        $global:syncFiles = $global:syncFiles + 1
    }
}

#
if ((Test-Path -LiteralPath $sourcePath) -eq $false) {
    $result = show_message -msgBody "" -msgTitle "" -msgImage "Warning"
    return;
}

#
if ((Test-Path -LiteralPath $smbPath) -eq $true) {
    $targetBasePath = $smbPath;
}

#
Get-ChildItem -LiteralPath $sourcePath | ForEach-Object {
    $date = "{0:yyyy_MM_dd}" -f $_.LastWriteTime
    $year = $date.Substring(0, 4)

    #JPG Files
    if ($jpgFiles -contains $_.Extension) {
        $targetPath = "$targetBasePath\\JPG\$year\$date"
        move_file -SourceFile $_.FullName -TargetFile "$targetPath\$_" -KeepSource $keepSourceFile
    }

    #Raw Files
    if ($rawFiles -contains $_.Extension) {
        $targetPath = "$targetBasePath\\RAW\$year\$date"
        move_file -SourceFile $_.FullName -TargetFile "$targetPath\$_" -KeepSource $keepSourceFile
    }

    #Video Files
    if ($videoFiles -contains $_.Extension) {
        $targetPath = "$targetBasePath\\$date"
        move_file -SourceFile $_.FullName -TargetFile "$targetPath\$_" -KeepSource $keepSourceFile
    }

}

if ($syncFiles -gt 0) {
    $result = show_message -msgBody " $syncFiles " -msgTitle "" -msgButton "OK" -msgImage "Info"
    #
    explorer.exe $targetBasePath
} else {
    $result = show_message -msgBody "?" -msgTitle "" -msgButton "YesNo" -msgImage "Question"
    if ($result -eq "Yes") {
        #
        explorer.exe $targetBasePath
    }
}
